mapper.update:
update collection
set user_id = #{userId,jdbcType=INTEGER},
  course_id = #{courseId,jdbcType=INTEGER},
  video_id = #{videoId,jdbcType=INTEGER}
where collection_id = #{collectionId,jdbcType=INTEGER}

mapper.select:
select collection_id, user_id, course_id, video_id
from collection
where collection_id = #{collectionId,jdbcType=INTEGER}
