mapper.insert:
insert into comment (comment_id, user_id, course_id, 
  date, parent_id, content
  )
values (#{commentId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{courseId,jdbcType=INTEGER}, 
  #{date,jdbcType=DATE}, #{parentId,jdbcType=INTEGER}, #{content,jdbcType=LONGVARCHAR}
  )

mapper.select:
select comment_id, user_id, course_id, date, parent_id, content
from comment
where comment_id = #{commentId,jdbcType=INTEGER}

select comment_id, user_id, course_id, date, parent_id, content
from comment
where course_id = #{courseId,jdbcType=INTEGER}
