mapper.update:
update teacher
set date = #{date,jdbcType=TIMESTAMP},
  sign = #{sign,jdbcType=VARCHAR},
  photo_url = #{photoUrl,jdbcType=VARCHAR}
where teacher_id = #{teacherId,jdbcType=INTEGER}

mapper.select:
select teacher_id, date, sign, photo_url
from teacher
where teacher_id = #{teacherId,jdbcType=INTEGER}
